import React from "react";

const handleChange=(event)=>{
    console.log(event.target)
    const number = document.getElementById("button-number").innerText
    if(event.target.id === "button-sub"){
        if(number === "0") return
        document.getElementById("button-number").innerText = (Number.parseInt(number) - 1).toString()

    }else{
        if(event.target.id === "button-add"){
            document.getElementById("button-number").innerText = (Number.parseInt(number) + 1).toString()
        }
    }
}

function CountingButton(){
    return(
        <div>
            <button className="button" id="button-sub" onClick={handleChange}>-</button>
            <button className="button" id="button-number" onClick={handleChange}>0</button>
            <button className="button" id="button-add" onClick={handleChange}>+</button>
        </div>

    )
}

export default CountingButton
