import React, {useState} from "react";

function CountingButton(props){
    console.log(props)
    const [amount, setAmount] = useState(props.initialAmount)

    const increase = () => {
        let newAmount = amount + 1
        setAmount(newAmount)
        props.setNewAmount({id: props.id, amount: amount})
    }

    const decrease = () => {
        let newAmount = amount - 1
        setAmount(newAmount)
        props.setNewAmount({id: props.id, amount: amount})
    }


    return(
        <div>
            <button className="button" id={id1} onClick={decrease}>-</button>
            <button className="button" id={id2}>{amount}</button>
            <button className="button" id={id3} onClick={increase}>+</button>
        </div>

    )
}

export default CountingButton
